mapper.select:


     and g.CODE_ID like concat('%', #{codeId}, '%')
     and g.SAMPLE_NUMBER = #{sampleNumber}
     and g.PROVINCIAL_LEVEL_NAME like concat('%', #{provincialLevelName}, '%')
     and g.CITY_LEVEL_NAME like concat('%', #{cityLevelName}, '%')
     and g.COUNTY_LEVEL_NAME like concat('%', #{countyLevelName}, '%')
     and g.SURVEY_TIME = #{surveyTime}
     and g.LONGITUDE = #{longitude}
     and g.LATITUDE = #{latitude}
     and g.ELEVATION = #{elevation}
     and g.TOPOGRAPHY_TYPE = #{topographyType}
     and g.SLOPE_POSITION_TYPE = #{slopePositionType}
     and g.ASCEPT_TYPE = #{asceptType}
     and g.FALLING_GRADIENT = #{fallingGradient}
     and g.SOIL_TYPE = #{soilType}
     and g.GRASSLAND_TYPE = #{grasslandType}
     and g.VEGETATION_COVERAGE = #{vegetationCoverage}
     and g.ABOVE_GROUND_BIOMASS = #{aboveGroundBiomass}
     and g.SURFACE_FEATURES_TYPE = #{surfaceFeaturesType}
     and g.MIAN_BOTANY_TYPE = #{mianBotanyType}
     and g.NATURAL_ENEMY_TYPE = #{naturalEnemyType}
     and g.NAME like concat('%', #{name}, '%')
     and g.PHOTO_URL = #{photoUrl}
     and g.STAUTE = #{staute}
     and g.CREATE_BY = #{createBy}
     and g.CREATE_TIME = #{createTime}
     and g.UPDATE_BY = #{updateBy}
     and g.UPDATE_TIME = #{updateTime}
     and g.DEPT = #{dept}
     and g.CODE = #{code}

order by g.CREATE_TIME desc

mapper.update:
update c_ground_survey
CODE_ID = #{codeId}, SAMPLE_NUMBER = #{sampleNumber}, PROVINCIAL_LEVEL_NAME = #{provincialLevelName}, CITY_LEVEL_NAME = #{cityLevelName}, COUNTY_LEVEL_NAME = #{countyLevelName}, SURVEY_TIME = #{surveyTime}, LONGITUDE = #{longitude}, LATITUDE = #{latitude}, ELEVATION = #{elevation}, TOPOGRAPHY_TYPE = #{topographyType}, SLOPE_POSITION_TYPE = #{slopePositionType}, ASCEPT_TYPE = #{asceptType}, FALLING_GRADIENT = #{fallingGradient}, SOIL_TYPE = #{soilType}, GRASSLAND_TYPE = #{grasslandType}, VEGETATION_COVERAGE = #{vegetationCoverage}, ABOVE_GROUND_BIOMASS = #{aboveGroundBiomass}, SURFACE_FEATURES_TYPE = #{surfaceFeaturesType}, MIAN_BOTANY_TYPE = #{mianBotanyType}, NATURAL_ENEMY_TYPE = #{naturalEnemyType}, NAME = #{name}, PHOTO_URL =#{photoUrl}, STAUTE = #{staute}, MAP_ID = #{mapId}, CREATE_BY = #{createBy}, CREATE_TIME = #{createTime}, UPDATE_BY = #{updateBy}, UPDATE_TIME = #{updateTime}, DEPT = #{dept}, CODE = #{code},
where GROUND_ID = #{groundId}
