mapper.select:
select shpfile_id, parent_id, ancestors, shpFile_name, shpFile_path, shp_color, order_num, status, del_flag, create_by, fileName, create_time from sys_shpFile where 1=1 -- shpFile_name like concat('%', '.shp', '%')
and shpFile_name like concat('%', #{shpFileName}, '%')
AND ancestors like concat('%', #{ancestors}, '%')
AND parent_id = #{parentId}
AND shpFile_name like concat('%', #{shpFileName}, '%')
AND status = #{status}
AND del_flag = #{delFlag}

select shpfile_id, parent_id, ancestors, shpFile_name, shpFile_path, shp_color, order_num, status, del_flag, create_by, create_time from sys_shpFile where del_flag = '0' -- AND shpFile_name like concat('%', '.shp', '%')
AND ancestors like concat('%', #{ancestors}, '%')
AND parent_id = #{parentId}
AND shpFile_name like concat('%', #{shpFileName}, '%')
AND status = #{status}
${params.dataScope} order by parent_id, order_num

select count(1) from sys_shpFile
where del_flag = '0'
and shpFile_id = #{shpFileId}
and parent_id = #{parentId}

select shpFile_id,shpFile_name,shpFile_path,shp_color from sys_shpFile
where shpFile_path in

    #{item.shpFilePath}

mapper.update:
update sys_shpFile
parent_id = #{parentId}, shpFile_name = #{shpFileName}, shpFile_path = #{shpFilePath}, shp_color = #{shpColor}, ancestors = #{ancestors}, order_num = #{orderNum}, status = #{status}, update_by = #{updateBy}, update_time = sysdate()
where shpFile_id = #{shpFileId}

update sys_shpFile set ancestors =

    when #{item.shpFileId} then #{item.ancestors}

where shpFile_id in

    #{item.shpFileId}

mapper.delete:
update sys_shpFile
set del_flag = '2'
where shpFile_id = #{shpFileId}
