select.where:
RESOLVE_ID is not null
and YEAR = #{year}
and PROVINCE = #{province}

mapper.select:
SELECT * FROM t_task_report

    YEAR = #{year}
    and PROVINCE = #{province}
    and STATUS = #{status}
    and CITY = #{deptName}
    and ADDRESS = #{addrCity}
    and ADDRESS = #{dictLabel}

AND REPORT_LEVEL = '1' AND DEL_STATUS = '1' order by CREATE_TIME desc

SELECT SUM( ifnull( JM_SIZE, '0' ) ) AS JM_SIZE, SUM( ifnull( JM_MONEY, '0' ) ) AS JM_MONEY, SUM( ifnull( XM_SIZE, '0' ) ) AS XM_SIZE, SUM( ifnull( XM_MONEY, '0' ) ) AS XM_MONEY, SUM( ifnull( HQLX_SIZE, '0' ) ) AS HQLX_SIZE, SUM( ifnull( HQLX_MONEY, '0' ) ) AS HQLX_MONEY, SUM( ifnull( RGSCD_SIZE, '0' ) ) AS RGSCD_SIZE, SUM( ifnull( RGSCD_MONEY, '0' ) ) AS RGSCD_MONEY, SUM( ifnull( HSPJ_SIZE, '0' ) ) AS HSPJ_SIZE, SUM( ifnull( HSPJ_MONEY, '0' ) ) AS HSPJ_MONEY, SUM( ifnull( ZY_MONEY, '0' ) ) AS ZY_MONEY, SUM( ifnull( DF_MONEY, '0' ) ) AS DF_MONEY, SUM( ifnull( SMHZL_SIZE, '0' ) ) AS SMHZL_SIZE, SUM( ifnull( SMHZL_MONEY, '0' ) ) AS SMHZL_MONEY, SUM( ifnull( THCYGL_SIZE, '0' ) ) AS THCYGL_SIZE, SUM( ifnull( THCYGL_MONEY, '0' ) ) AS THCYGL_MONEY, SUM( ifnull( HTT_SIZE, '0' ) ) AS HTT_SIZE, SUM( ifnull( HTT_MONEY, '0' ) ) AS HTT_MONEY, SUM( ifnull( YKCYZL_SIZE, '0' ) ) AS YKCYZL_SIZE, SUM( ifnull( YKCYZL_MONEY, '0' ) ) AS YKCYZL_MONEY, SUM( ifnull( DHC_SIZE, '0' ) ) AS DHC_SIZE, SUM( ifnull( DHC_MONEY, '0' ) ) AS DHC_MONEY FROM t_task_report
YEAR = #{year} and PROVINCE = #{province}
AND REPORT_LEVEL = '1' AND DEL_STATUS = '1' AND STATUS = '4'

SELECT * FROM t_task_report

    ADDRESS = #{dictLabel}
    and YEAR = #{year}
    and PROVINCE = #{province}
    and `STATUS` in (SELECT `STATUS` from t_task_report WHERE `STATUS`='2' or `STATUS`='3' or `STATUS`='4' or
    `STATUS`='5') AND REPORT_LEVEL = '0' AND DEL_STATUS = '1' order by CREATE_TIME desc


WHERE
REPORT_LEVEL = '1'
AND DEL_STATUS = '1'
AND PROVINCE = #{province}
AND YEAR = #{year}

mapper.update:
update t_task_report
RESOLVE_ID = #{resolveId}, REPORT_LEVEL = #{reportLevel}, YEAR = #{year}, PROVINCE = #{province}, CITY = #{city}, ADDRESS = #{address}, ADDRESS_ID = #{addressId}, TIME_SCALE = #{timeScale}, JM_SIZE = #{jmSize}, JM_MONEY = #{jmMoney}, XM_SIZE = #{xmSize}, XM_MONEY = #{xmMoney}, HQLX_SIZE = #{hqlxSize}, HQLX_MONEY = #{hqlxMoney}, SMHZL_SIZE = #{smhzlSize}, SMHZL_MONEY = #{smhzlMoney}, THCYGL_SIZE = #{thcyglSize}, THCYGL_MONEY = #{thcyglMoney}, RGSCD_SIZE = #{rgscdSize}, RGSCD_MONEY = #{rgscdMoney}, HSPJ_SIZE = #{hspjSize}, HSPJ_MONEY = #{hspjMoney}, HTT_SIZE = #{httSize}, HTT_MONEY = #{httMoney}, DHC_SIZE = #{dhcSize}, DHC_MONEY = #{dhcMoney}, YKCYZL_SIZE = #{ykcyzlSize}, YKCYZL_MONEY = #{ykcyzlMoney}, ZY_MONEY = #{zyMoney}, DF_MONEY = #{dfMoney}, START_TIME = #{startTime}, END_TIME = #{endTime}, CREATE_TIME = #{createTime}, CREATE_BY = #{createBy}, UPDATE_TIME = #{updateTime}, UPDATE_BY = #{updateBy}, STATUS = #{status}, DEL_STATUS = #{delStatus}, AUDIT_ID = #{auditId}, AUDIT_NAME = #{auditName}, AUDIT_TIME = #{auditTime}, CONCRETE_TIME = #{concreteTime}, AUDIT_STATUS = #{auditStatus},
where REPORT_ID = #{reportId}
